import { Box, Button, Flex, Image, Text, VStack } from "@chakra-ui/react";

export const CharCard = ({ character }) => {
  return (
    <>
      <Box>
        <VStack
          borderRadius="xl"
          borderColor="white"
          borderWidth="1px"
          shadow="xl"
          h="500px"
        >
          <Flex w="100%" justifyContent="center" bg="red">
            <Image
              src={character.image}
              alt={character.name}
              w="100%"
              h="250px"
              alignSelf="center"
            />
          </Flex>
          <Text>Nome: {character.name}</Text>
          <Text>Gênero: {character.gender}</Text>
          <Text>Espécie: {character.species}</Text>
          <Button>Ver mais</Button>
        </VStack>
      </Box>
    </>
  );
};
