import { Box, Flex, Heading, HStack, Spacer } from "@chakra-ui/react";
import axios from "axios";
import { useEffect, useState } from "react";
import { CharCard } from "../CharCard";
import Loader from "react-loader-spinner";

export const List = () => {
  const [charactersList, setCharactersList] = useState([]);
  const [link, setLink] = useState("https://rickandmortyapi.com/api/character");
  const [loading, setLoading] = useState(false);
  const nextPage = () => {
    fetch(link)
      .then((response) => response.json())
      .then(async (response) => {
        if (response.info.next) {
          await setLink(response.info.next);
        }
      })
      .catch((err) => console.log(err));
  };

  const previousPage = () => {
    fetch(link)
      .then((response) => response.json())
      .then(async (response) => {
        if (response.info.prev) {
          await setLink(response.info.prev);
        }
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    setLoading(true);
    fetch(link)
      .then((response) => response.json())
      .then(async (response) => {
        await setCharactersList(response.results);
        setLoading(false);
      })
      .catch((err) => console.log(err));
  }, [link]);

  return (
    <>
      <Box>
        <Flex flexWrap="wrap">
          {charactersList.map((character) => (
            <>
              <CharCard character={character} />
              <Spacer />
            </>
          ))}
        </Flex>
        <button onClick={previousPage}>Previous</button>
        <button onClick={nextPage}>Next</button>
        <Loader />
      </Box>
    </>
  );
};
