import { Box, Flex, Heading, HStack, Spacer } from "@chakra-ui/react";
import axios from "axios";
import { useEffect, useState } from "react";
import { CharCard } from "../CharCard";

export const List = () => {
  const [charactersList, setCharactersList] = useState([]);
  const [link, setLink] = useState("https://rickandmortyapi.com/api/character");

  const nextPage = () => {
    fetch(link)
      .then((response) => response.json())
      .then((response) => {
        return response.info.next ? setLink(response.info.next) : null;
      })
      .catch((err) => console.log(err));
  };

  const previousPage = () => {
    fetch(link)
      .then((response) => response.json())
      .then((response) => {
        if (response.info.prev) {
          setLink(response.info.prev);
        }
      })
      .catch((err) => console.log("Deu errooooooo"));
  };

  useEffect(() => {
    fetch(link)
      .then((response) => response.json())
      .then((response) => setCharactersList(response.results))
      .catch((err) => console.log(err));
  }, [link]);

  return (
    <>
      <Box>
        <Flex>
          {charactersList.map((character) => (
            <CharCard character={character} />
          ))}
        </Flex>
        <button onClick={previousPage}>Previous</button>
        <button onClick={nextPage}>Next</button>
      </Box>
    </>
  );
};
